INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (1, 'b01441d690b4b5e4000f2fa90ba84268', 1, 'Court Sneakers', '<ul><li><span style="color: rgb(89, 89, 89); background-color: transparent;">Inspired by retro tennis sneakers</span></li><li><span style="color: rgb(89, 89, 89); background-color: transparent;">Usually constructed with lace-up closures, though velcro can also be found.</span></li><li><span style="color: rgb(89, 89, 89); background-color: transparent;">Two facing quarters form the upper, starting at the sole, while the tongue extends seamlessly into the toe box.</span></li><li>Mostly low-top, but can be high-top</li><li><span style="color: rgb(89, 89, 89); background-color: transparent;">Usually designed with a flat, low-profile sole made of rubber.</span></li><li><span style="color: rgb(89, 89, 89); background-color: transparent;">The upper is generally made from leather or suede, with a clean silhouette.</span></li></ul><p><br></p><p><span style="color: rgb(89, 89, 89); background-color: transparent;">Not to confuse with:</span></p><ul><li><strong style="color: rgb(89, 89, 89); background-color: transparent;">Canvas Trainers:</strong><span style="color: rgb(89, 89, 89); background-color: transparent;"> Similar in shape, but in canvas with a rubber toe-cap.</span></li><li><strong style="color: rgb(89, 89, 89); background-color: transparent;">Plimsolls: </strong><span style="color: rgb(89, 89, 89); background-color: transparent;">A</span>lways have<strong> </strong>a vamp insert until the throat line. Mostly in canvas or jersey upper.</li><li><strong style="color: rgb(89, 89, 89); background-color: transparent;">Retro Football Sneakers:</strong><span style="color: rgb(89, 89, 89); background-color: transparent;"> Wider shape, without </span><span style="color: rgb(89, 89, 89);">two facing quarters, </span><span style="color: rgb(89, 89, 89); background-color: transparent;">generally have a suede toe-cap.</span></li><li><strong style="color: rgb(89, 89, 89); background-color: transparent;">Retro Basketball Sneakers:</strong><span style="color: rgb(89, 89, 89); background-color: transparent;"> Chunkier design. </span>The toe part has a perforated overlay piece of leather covering the frontal and side parts of the toe box.</li></ul>', '30ed613f2621303bd1ef1487a97a1450');
