INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (2, '71e83ca83dd815fb28599128e3923a04', 1, 'Performance Running Sneakers', '<ul><li>Technical and profiled sneakers, usually made from lightweight, breathable mesh or engineered knit</li><li>Slight to pronounced cushioning system, with slightly elevated heel-to-toe drop</li><li>The sole may show a gap between the toes and heel</li></ul><p><br></p><p>Not to be confused with:</p><ul><li><strong>Lifestyle Running Sneakers:</strong> Chunkier midsoles for style rather than propulsion. Leather, suede, or fashion-driven uppers (less breathable, less flexible).</li><li><strong>Retro Running Sneakers:</strong> Inspired by vintage performance models, not showing advanced technology, built for casual wear. Uppers in less performance-oriented materials.</li><li><strong>Retro Trainers:</strong> Heavier design and soles, also known as "Dad Sneakers", often showing a visible separation between heel and forefoot. Complex uppers with elaborated material mixes.</li><li><strong>Trail Sneakers:</strong> More robust design, with bulkier outsoles with visible lugs. Often paired with a hiking lacing system.</li></ul>', '286d7e88e45d439883e45b3cbac97102');
