INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (3, 'c5fbe83521262d32a09cfb88ac49e3aa', 1, 'Retro Running Sneakers', '<ul><li>Inspired by 1970s running shoes</li><li>They have an almond-shaped toecap</li><li>Layered uppers are made from leather, suede, or mixed materials</li><li>There is a distinct overlays on the toe box and heel counter</li><li>They have a low-profile sole sloping from thin heel to ultra-thin toe</li><li>Lace-up closure</li></ul><p><br></p><p>Not to be confused with:</p><ul><li><strong>Lifestyle Running Sneakers:</strong> Chunkier midsoles, more technical uppers</li><li><strong>Retro Football Sneakers:</strong> Flat sole, no slope at the front</li><li><strong>Retro Trainers:</strong> Heavier build, thick soles, complex mixed-material uppers</li><li><strong>Retro Basketball Sneakers:</strong> Flat soles, flat toe cap, leather uppers with perforations</li></ul>', 'fd6c941c4752bdf41417555c6c1fb6b0');
