INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (5, '787a070c951528efc2c21df59c5144d4', 1, 'Performance Football Sneakers', '<ul><li>Made to play professional Football</li><li>They have a slim, profiled shape</li><li>The uppers in knit or leather, with textured effects and padded topstitching</li><li>Large visible crampons or lug soles, in varied designs</li><li>Lace-up closure, sometimes with integrated ankle-sock knit</li><li>Bold logos, graphic lines, and color-blocking</li></ul><p><br></p><p>Not to be confused with:</p><ul><li><strong>Boxing Sneakers:</strong> Higher, rigid ankle not suited for flexibility</li><li><strong>Retro Football Sneakers:</strong> Suede uppers with no visible crampons</li></ul>', 'ebb214eb6c2858482481d66cd4dc258f');
