INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (6, '5c83da85587955f3ee36cbf0156fe68a', 1, 'Retro Football Sneakers', '<ul><li>They are made from leather and suede only, no technical materials</li><li>They have a topstitched upper for a lightly padded look</li><li>They often feature a contrasting toe cap and sometimes an extra-long tongue</li><li>They have a straight, thin rubber sole, usually in brown, black, or off-white</li></ul><p><br></p><p>Not to be confused with:</p><ul><li><strong>Performance Football Sneakers:</strong> Technical materials, ankle socks, and visible cleats</li><li><strong>Skater Sneakers:</strong> Thicker soles, wide laces</li><li><strong>Plimsolls:</strong> Vamp insert extends up to the throat line</li><li><strong>Slip-On Sneakers:</strong> No visible closure, thicker rubber sole</li></ul>', '5a0b112005d4b5acc1eed4e597ce6cf5');
