INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (7, '6a48461da44869966cc30a0d4bacd3ac', 1, 'Performance Basketball Sneakers', '<ul><li>They feature leather or technical uppers such as mesh or nylon, designed for breathability, support, and durability while playing basketball</li><li>They come in high or low top styles; high tops typically have tongues that rise higher than the back collar</li><li>They often feature a top line with a deep curve, especially on high-cut designs for better ankle mobility</li><li>They are designed with lace-up closures, often reinforced for a secure fit during gameplay</li><li>They feature layered, textured, or bubble-like soles engineered to provide traction, cushioning, and stability on indoor courts</li></ul><p><br></p><p>Not to be confused with:</p><ul><li><strong>Performance Running Shoes</strong>: Inclined soles with minimal ankle support</li><li><strong>Canvas Sneakers</strong>: Canvas uppers paired with flat rubber soles</li><li><strong>Retro Basketball Sneakers</strong>: Leather uppers with design references to court shoes, but lacking modern performance features</li><li><strong>Performance Football Sneakers</strong>: Visible cleats and sock-like collar details designed for turf or grass surfaces</li></ul>', '5f68769934c2df3278b0d7dc6c7e73ab');
