INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (8, 'c215714124d4d21b7046a491961563ee', 1, 'Retro Basketball Sneakers', '<ul><li>Features uppers in leather or rubber, often with perforations on the toe box</li><li>Designed with a lace-up closure, sometimes including extra eyelets at the collar (on high tops)</li><li>Available in high or low top styles; high tops typically have a higher front collar than back</li><li>They have a leather overlay on the toe, wrapping around the front and sides of the toe box</li></ul><p><br></p><p>Not to be confused with:</p><ul><li><strong>Performance Basketball Sneakers</strong>: Deep sole curves, more technical construction</li><li><strong>Retro Football Sneakers:</strong> Suede uppers with flat, contrasting soles</li><li><strong>Canvas Sneakers:</strong> Canvas uppers with rubber soles</li><li><strong>Boxing Sneakers:</strong> Higher overall cut with tighter ankle fit</li></ul>', 'c4f848d890d8aa9f01108ca35dd0795b');
