INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (9, 'bcdc6f40832f9424d5abc0bb4d71afec', 1, 'Retro Trainers', '<ul><li>Chunky, sculpted sole with carved lines and often a bridge between heel and forefoot</li><li>Layered uppers made from mesh, leather, suede, rubber, and plastic</li><li>Feature cut-outs, inserts, and graphic overlays in tone-on-tone or contrasting finishes</li><li>Design emphasizes a structured, rigid build with a retro yet technical look</li></ul><p><br></p><p>Not to be confused with:</p><ul><li><strong>Lifestyle Running Sneakers:</strong> Leather/suede uppers, less breathable</li><li><strong>Retro Running Sneakers:</strong> Simpler silhouette, casual use, non-technical materials</li><li><strong>Trail Sneakers: </strong>Bulkier outsoles with lugs, often with hiking-style lacing</li><li><strong>Performance Running Sneakers: </strong>Lightweight, breathable mesh, visible tech, flexible foam soles</li></ul>', '84dd9efcaedd344174f01e0128bf2255');
