INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (10, '375fcbaf17cddfb6621f85d6867afc52', 1, 'Plimsoll Sneakers', '<ul><li>They feature a low-cut profile that sits below the ankle, with a slim, lightweight upper made from canvas or similar textile materials</li><li>They are commonly designed with a simple eyelet-and-lace closure, offering secure fastening without additional structure or padding</li><li>They feature a thin, flat vulcanized rubber sole that offers basic grip and durability, with a distinct separation between the upper and sole</li><li>They always feature a vamp insert that extends all the way to the throat line, giving them their classic, minimal silhouette</li></ul><p><br></p><p>Not to be confused with:</p><ul><li><strong>Skater Shoes</strong>: Heavier and more cushioned, often with reinforced toe caps, padded collars, and thicker rubber outsoles</li><li><strong>Court Sneakers</strong>: Similar shape, but made out of leather or suede. Lacks the continuous vamp insert that reaches the throat line.</li><li><strong>Canvas Sneakers</strong>: Similar in material but lack the continuous vamp insert that reaches the throat line</li><li><strong>Retro Football Sneakers</strong>: Typically have suede uppers and flat, contrasting rubber soles, designed with sport styling</li></ul>', '17735d8a3a29225a0698eba2fdd3fffb');
