INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (11, 'd58953f72c82028197b1c4277051916f', 1, 'Skater Sneakers', '<ul><li>They feature flat, straight soles designed to maximize contact with the skateboard deck for grip and control</li><li>They feature slim uppers made from durable materials like canvas or suede, offering flexibility and board feel</li><li>They are designed with lace-up closures, usually with wide laces and padded tongues for comfort and easy adjustment</li><li>They feature low-cut padded collars to protect the ankle area during impact or tricks</li><li>They are built for skateboarding but commonly worn casually due to their simple, functional design</li></ul><p><br></p><p>Not to be confused with:</p><ul><li><strong>Retro Basketball Sneakers</strong>: Heavier leather builds with thicker soles</li><li><strong>Slip-On Sneakers</strong>: No laces, minimal support, and not suited for skateboarding</li><li><strong>Retro Football Sneakers</strong>: Suede uppers with slim soles</li><li><strong>Court Sneakers</strong>: Fully made out of leather with no padded tongues</li></ul>', 'e188c2550703df3f8cc3786165be6faf');
