INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (12, 'a13d656c744a18c9b4e2962518443201', 1, 'Canvas Trainers', '<ul><li>They feature uppers made from canvas fabric, often with suede or leather reinforcements for added durability and style</li><li>They have a flat rubber soles, though some models have chunky or irregular sole designs</li><li>They feature lace-up closures, but slip-on versions and alternatives like zippers or elastic gores also exist</li><li>They feature two facing quarters that rise from the sole and connect to a canvas tongue, forming the upper structure, including the vamp and toe box</li><li>They were originally designed as performance basketball shoes, now worn casually for their versatility and breathable materials</li></ul><p><br></p><p>Not to be confused with:</p><ul><li><strong>Plimsoll Sneakers</strong>: Feature canvas uppers but always include a vamp insert extending to the throat line and a more minimal, uniform construction</li><li><strong>Court Sneakers</strong>: Similar shape, but made out of leather or suede.</li><li><strong>Skater Sneakers</strong>: Heavier, with padded collars, suede uppers, and flat soles built specifically for skateboarding</li><li><strong>Retro Football Sneakers</strong>: Suede uppers and flat, contrasting soles.</li></ul>', '71aff8119b9bfdfb2a198673f9bbf419');
