INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (13, 'c085e48db7d67cddacf444c65048f2e5', 1, 'Slip-On Sneakers', '<ul><li>They feature no laces or fasteners, instead using elastic side gores or stretch panels for easy on-off and a secure fit</li><li>They are built with flat rubber soles, often vulcanized, offering durability, grip, and lightweight flexibility</li><li>They feature a one-piece vamp that extends across the top of the foot and connects directly to the quarters, with no separate tongue</li><li>They are always low-top and can be made from various materials, including canvas, leather, or synthetics</li></ul><p><br></p><p>Not to be confused with:</p><ul><li><strong>Plimsoll Sneakers</strong>: Feature lace-up closures and a distinct vamp insert reaching the throat line</li><li><strong>Sock Sneakers</strong>: Built from stretch-knit materials with a high-top, sock-like collar and sculpted midsoles.</li><li><strong>Moccasins</strong>: Typically made from soft leather with stitched uppers and little to no sole structure. Also, no stretch side panels.</li><li><strong>Clogs</strong>: Backless or semi-backless shoes, often with rigid uppers and thick, molded soles. Generally wider than a sneaker.</li></ul>', 'a48b0bfa0cf38e8608853fd65649db4f');
