INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (14, '6b4b779b443f5e1afdbfa10303ccb0d3', 1, 'Sock Sneakers', '<ul><li>They feature a laceless, pull-on construction that mimics the shape and fit of a high sock, made from stretch-knit materials</li><li>They are built on lightweight rubber soles, often chunky or sculpted, providing cushioning and flexibility</li><li>They feature seamless knit uppers that extend from the sole to the ankle (or higher), offering breathability and a snug, glove-like fit</li><li>They are designed with minimal external panels, relying on knit tension and the sole structure to maintain shape and support</li></ul><p><br></p><p>Not to be confused with:</p><ul><li><strong>Lifestyle Running Sneakers</strong>: May use knit uppers, but include laces, structured overlays, and performance-inspired design cues</li><li><strong>Performance Running Sneakers</strong>: Designed for athletic use with a technical design, reinforced uppers, arch support, and performance soles</li><li><strong>Sock Boots</strong>: Share a sock-like fit but often have heel construction, and boot shape</li><li><strong>Slip-On Sneakers</strong>: Low-cut with elastic gores but without the extended knit collar or sculpted soles</li></ul>', '6b587ad3f4f29db1b5daebb9355b2095');
