INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (93, '1664b8aae4b28af95e932f3f8e556f7a', 21, 'Combat Boots', '<ul><li>Have a lace-up closure, a frontal zipper closure or even velcro. These closures need to be in the center of the shoe.</li><li>They have a "bulky" look, usually accompanied by lug soles (chunky soles).</li><li>Can have buckle or zipper embellishments.</li><li>Can be any boot height, mostly mid-calf.</li></ul>', '5d43bfa1fbc208c3edad250aeca5c189');
