INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (94, 'c9c14b35cb08ce5be78bf0537790728e', 21, 'Hiking Boots', '<ul><li>Hiking boots have a padded back patch or top line.</li><li>They are usually laced with ski loop eyelets.</li><li>They have heavy soles, either textured or lug.</li><li>May have a chunky heel, that can go from flat to medium height.</li><li>Usually, they are a low calf height.</li><li>Hiking boots have a sturdy upper with materials like leather, suede, and heavy canvas, compared to trail sneakers, which use flexible/technical materials for the upper.</li></ul>', 'd674a2fe3319a58618dd831803b68533');
