INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (95, '42c09ad6ff6c553993d00cdffe1a7ec3', 21, 'Snow Boots', '<ul><li>Snow boots are fully padded or lined with a thick material such as fur or shearling.</li><li>They have a "bulky" look, with ropes or laces as closure, and usually have flat soles.</li><li>The upper is usually made of a waterproof material or suede, but it can also be made from fur or shearling.&nbsp;</li></ul>', '215dc01f2931aff61513871c0af9821f');
