INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (196, 'ec8612c1a141d7aa0820cbe33a97f451', 46, 'Jeans', '"Jeans" are a specific style of denim pants that include classic design elements like five pockets, rivets, a fly, and back yoke stitching. They can come in any color and a variety of fits, including "Skinny", "Flared", "Wide-Leg", "Straight", or "Bootcut".\nWhile all "Jeans" are made from denim, not all "Denim Pants" are "Jeans": "Jeans" must have the traditional construction details that define the style.\n"Denim Pants" without these characteristic features (e.g., lacking pockets or having a trouser-style cut) are not considered "Jeans", even if made from denim fabric.', '2c30a7fc70481c9c29554038cb0aac0f');
INSERT INTO trend_images (trend_id, image_type, md5_hash, description) VALUES (196, 'positive', 'c74aa315ba71900d5ecd2ac90ed9c638', 'This is Baggy Grey Jeans  fits the Jean style, even in a loose cut.');
INSERT INTO trend_images (trend_id, image_type, md5_hash, description) VALUES (196, 'positive', '1fda7610112941deb1134793ed236b1e', 'Jeans must have a back yoke and usually have back patch pockets.');
INSERT INTO trend_images (trend_id, image_type, md5_hash, description) VALUES (196, 'positive', '1ae14b3ccfd28392f2a64b96cc67b9c3', 'Jeans must include front scoop pockets, a coin pocket, and a fly (zip or button).');
INSERT INTO trend_images (trend_id, image_type, md5_hash, description) VALUES (196, 'positive', '76bae65dfebe6bb54219820f662670ef', 'These are Pink Jeans  Jeans can be any color.');
