INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (211, 'ee551719070ebcf86dd6607680b78e8b', 48, 'Wide-Leg Pants', '"Wide-Leg Pants" refers to all pants, regardless of fabric, that flare dramatically from the crotch. This fit differs from "Flared Pants" (which flare subtly from the crotch), "Fit & Flare" (the parent trend of "Bootcut" and "Bell Bottoms") which includes the pants that are fitted through the thigh and flare out from the knee, and "Baggy Pants", that are oversized pants with a low crotch and waist.\nThe mood board will include all "Wide-Leg Pants", in any fabric, closure, or styling, as long as the silhouette follows this shape', '277ab92afcfe5b5638b62c7ac48eac60');
