INSERT INTO trends (id, trend_id, vertical_id, name, description, image_hash) VALUES (365, 'bf5cca2b9f7716c834b55f68fe160cd8', 89, 'Checked', '<ul><li>Checked print is formed by intersecting horizontal and vertical lines that create repeated squares or rectangles.</li><li>This is a <strong>parent</strong> category that encompasses the prints <strong>Gingham, Plaid, Prince of Wales, Windowpane, Checkerboard, Lumberjack, and Argyle</strong>.</li><li>It can be used on any garment, in any fabric, and any color, making it one of the most versatile design motifs in fashion.</li><li>To be considered a checked print, a pattern must feature a <strong>grid-based structure</strong>. Designs that dont follow this format are not classified as checks.</li></ul>', 'b86df507765f8e3b62447ed70a4036f8');
